import React, { Component } from "react";
// import TextInput from "../../components/searchInput/searchInput"
// import SearchButton from "../../components/button/button"
import DisplayCard from "../../components/card/card"
import { Row, Col, Container, Card, Button } from "react-bootstrap";
import API from "../../utils/API"
import TextInput from "../../components/searchInput/searchInput"

class Search extends Component {
  constructor(props) {
    super(props);
    this.state = {
      search: ""

    };
  }
    searchForBook = (book) => {
      API.getBook(book).then((res) => {
        console.log(res)
      })
      .catch((err) => console.log(err)) 
    }
    handleInputChange = (event) => {
      
      const value = event.target.value;     
      this.setState({
        ...this.state,
        search: value
      });
      console.log(this.state.search)
    };
    handleButtonClick = (event) => {
      event.preventDefault();
      this.searchForBook();
    };
    handleFormSubmit = (event) => {
      this.searchForBook(this.state.search)
    }


render () {
    return (
      <div>
        <h1>Search books</h1>
      <Container>
<Row>
<Col>
<DisplayCard />
<TextInput
            value={this.state.search}
            handleInputChange={this.handleInputChange}
            handleFormSubmit={(event) => this.handleFormSubmit(event)} />
            <SearchButton />
</Col>
</Row>
</Container>
   
<Container>
<Row>
<Col>
<Card>
