import React, { Component } from "react";
// import TextInput from "../../components/searchInput/searchInput"
// import SearchButton from "../../components/button/button"
import DisplayCard from "../../components/card/card"
import { Row, Col, Container, Card, Button } from "react-bootstrap";
import API from "../../utils/API"
import TextInput from "../../components/searchInput/searchInput"
import SearchButton from "../../components/button/button"


class Search extends Component {
  constructor(props) {
    super(props);
    this.state = {
      search: "",
      bookRes: []

    };
  }
    searchForBook = (book) => {
      API.getBook(book).then((res) => {
        for (var i =0; i < res.data.items; i++){
          var bookres=[];
        bookres.push({name: res.data['items' + [i]].volumeInfo.title})
        }
        
        // console.log("hit")
        // console.log(res.data.items[0].volumeInfo.title)
        this.setState({...this.state, bookRes: bookres})
        console.log(this.state.bookRes)
        
      })
      .catch((err) => console.log(err)) 
    }
    handleInputChange = (event) => {
      
      const value = event.target.value;     
      this.setState({
        ...this.state,
        search: value
      });
      // console.log(this.state.search)
    };
    handleButtonClick = (event) => {
      event.preventDefault();
      this.searchForBook();
    };
    handleFormSubmit = (event) => {
      // console.log("poop")
      this.searchForBook(this.state.search)

    }


render () {
    return (
      <div>
        <h1>Search books</h1>
      <Container>
<Row>
<Col>

<TextInput
    value={this.state.search}
    handleInputChange={this.handleInputChange}
    handleFormSubmit={(event) => this.handleFormSubmit(event)} 
    />
    {/* <SearchButton 
    onClick={(event) => this.handleFormSubmit(event)}
    /> */}
</Col>
</Row>
</Container>
   
<Container>
<Row>
<Col>

<Card style={{ width: '18rem' }}>
  <Card.Img variant="top" src="holder.js/100px180" />
  <Card.Body>
    <Card.Title>Card Title</Card.Title>
    <Card.Text>
    {this.state.bookRes.map((book) => {
  console.log('hit')
 
    
        {book.name}
      
  

})}
    </Card.Text>
    <Button variant="primary">Go somewhere</Button>
  </Card.Body>
</Card>
  

<Card 

// onClick={this.set to API}
>
  <Card.Text
  style={{ width: '36rem', height: '8rem' }}>
  {this.state.bookRes.map((book) => {
  console.log('hit')
  return (
    <ul>
      <li>
        {book.name}
      </li>
      <li>
        test
      </li>
    </ul>
  )

})}
  </Card.Text>

</Card>  
  
            


</Col>
</Row>
</Container>   
   


</div>
     
    )
  }
}
    

export default Search;
