import React, { Component } from "react";
// import TextInput from "../../components/searchInput/searchInput"
// import SearchButton from "../../components/button/button"
import DisplayCard from "../../components/card/card"
import { Row, Col, Container, Card, Button } from "react-bootstrap";
import API from "../../utils/API"
import TextInput from "../../components/searchInput/searchInput"
import SearchButton from "../../components/button/button"

class Search extends Component {
  constructor(props) {
    super(props);
    this.state = {
      search: "",
      bookRes: []

    };
  }
    searchForBook = (book) => {
      API.getBook(book).then((res) => {
        // console.log(res.data.items)
        var bookres=[];
        for (var i = 0; i < res.data.items.length; i++) {
          bookres.push({id: [i], name: res.data.items[i].volumeInfo.title, pic: res.data.items[i].volumeInfo.imageLinks.smallThumbnail, descript: res.data.items[i].volumeInfo.description})
          // console.log('hit')
        }
        // console.log(res.data.items[1].volumeInfo.description)
        // console.log(bookres)
        
        this.setState({...this.state, bookRes: bookres})
        // console.log(this.state.bookRes)
        
      })
      .catch((err) => console.log(err)) 
    }
    postFav = (event) => {
      var favbook = event.target.id;
      console.log(favbook)
      // event.preventDefault();
// console.log('hello world')
    }
    handleInputChange = (event) => {
      
      const value = event.target.value;     
      this.setState({
        ...this.state,
        search: value
      });
      // console.log(this.state.search)
    };
    handleButtonClick = (event) => {
      event.preventDefault();
      this.searchForBook();
    };
    handleFormSubmit = (event) => {
      // console.log("poop")
      this.searchForBook(this.state.search)

    }


render () {
    return (
      <div>
        <h1>Search books</h1>
      <Container>
<Row>
<Col>

<TextInput
    value={this.state.search}
    handleInputChange={this.handleInputChange}
    handleFormSubmit={(event) => this.handleFormSubmit(event)} 
    />
    {/* <SearchButton 
    onClick={(event) => this.handleFormSubmit(event)}
    /> */}
</Col>
</Row>
</Container>
   
<Container>
<Row>
<Col>

<Card>
{this.state.bookRes.map((book) => {
  // console.log('hit')
  return (
    <div>
      <ul>
        <li>
          <Card>
          <Card.Img style={{ width: '8rem', height: '8rem' }}     
     src={book.pic} /> 
    <Card.Title>{book.name}</Card.Title>      
   <Card.Body>{book.descript}</Card.Body>
   <Button 
   onClick={() => this.postFav()}
   variant="primary">Fav</Button>
          </Card>
        
        </li>      
      </ul>
      <br />
    </div>
    
  )
})}
</Card>

{/* {this.state.bookRes.map((book) => {
  console.log('hit')
  return (
<DisplayCard 
text={book.name}
// onClick={this.set to API}
>
  
</DisplayCard>  
  )
            
})} */}

</Col>
</Row>
</Container>   
   


</div>
     
    )
  }
}
    

export default Search;
