import React, { Component } from "react";
// import TextInput from "../../components/searchInput/searchInput"
// import SearchButton from "../../components/button/button"
import DisplayCard from "../../components/card/card"
import { Row, Col, Container, Card, Button } from "react-bootstrap";
import API from "../../utils/API"
import TextInput from "../../components/searchInput/searchInput"
import SearchButton from "../../components/button/button"

class Search extends Component {
  constructor(props) {
    super(props);
    this.state = {
      search: "",
      bookRes: []

    };
  }
    searchForBook = (book) => {
      API.getBook(book).then((res) => {
        // console.log("hit")
        // console.log(res.data.items[0].volumeInfo.title)
        this.setState({...this.state, bookRes: res.data.items[0].volumeInfo.title})
        console.log(this.state.bookRes)
        
      })
      .catch((err) => console.log(err)) 
    }
    handleInputChange = (event) => {
      
      const value = event.target.value;     
      this.setState({
        ...this.state,
        search: value
      });
      // console.log(this.state.search)
    };
    handleButtonClick = (event) => {
      event.preventDefault();
      this.searchForBook();
    };
    handleFormSubmit = (event) => {
      // console.log("poop")
      this.searchForBook(this.state.search)

    }


render () {
    return (
      <div>
        <h1>Search books</h1>
      <Container>
<Row>
<Col>

<TextInput
    value={this.state.search}
    handleInputChange={this.handleInputChange}
    handleFormSubmit={(event) => this.handleFormSubmit(event)} 
    />
    {/* <SearchButton 
    onClick={(event) => this.handleFormSubmit(event)}
    /> */}
</Col>
</Row>
</Container>
   
<Container>
<Row>
<Col>


  
{this.state.bookRes.map((book) => {
  console.log('hit')
  return (
<DisplayCard text={book.name}>
  <ul>
    <li>{book.name}

    </li>
  </ul>
</DisplayCard>  
  )
            
})}

</Col>
</Row>
</Container>   
<Container>
<Row>
<Col>
<Card>
  this will be filled
  <Button>save</Button>
</Card>
</Col>
</Row>
</Container>   


</div>
     
    )
  }
}
    

export default Search;
